mapper.update:

UPDATE attends
SET is_refundable = false
WHERE lecture_id = #{lectureId} AND member_id = #{memberId}

mapper.insert:

		INSERT INTO lectures (
            member_id, title, profile, description, description_pic, category, 
            price, link	, limit_student, deadline_time, start_date, end_date
        ) VALUES (
            #{memberId}, #{title}, #{profileUrl}, #{description}, #{descriptionPicUrl}, #{category}, 
            #{price}, #{link}, #{limitStudent}, #{deadlineTime}, #{startDate}, #{endDate}
        )

mapper.update:
UPDATE lectures
title = #{title}, profile = #{profileUrl}, description = #{description}, description_pic = #{descriptionPicUrl}, category = #{category}, price = #{price}, status = #{status}, link = #{link}, limit_student = #{limitStudent}, deadline_time = #{deadlineTime}, lectures_date = #{lecturesDate}, start_date = #{startDate}, end_date = #{endDate}, deleted = #{deleted}
WHERE lecture_id = #{lectureId}

mapper.delete:

UPDATE lectures
SET deleted = true
WHERE lecture_id = #{lectureId} AND member_id = #{memberId}

DELETE FROM lectures_tags
WHERE lecture_id = #{lectureId} 
AND tag_id IN 

    #{tagId}
